package com.example.microcalculate;import androidx.appcompat.app.AppCompatActivity;import android.annotation.SuppressLint;import android.content.Intent;import android.graphics.Color;import android.net.Uri;import android.os.Bundle;import android.util.Log;import android.view.Menu;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;import android.widget.Toast;public class MainActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        clickListener();    }    public void clickListener() {        Button sendWishes = findViewById(R.id.sendWishes);        EditText wishes = findViewById(R.id.enterText);        Button send = findViewById(R.id.send);        sendWishes.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                sendWishes.setVisibility(View.INVISIBLE);                wishes.setVisibility(View.VISIBLE);                wishes.setCursorVisible(true);                send.setVisibility(View.VISIBLE);            }        });        send.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                sendEmail();            }        });    }        @SuppressLint("IntentReset")        protected void sendEmail(){            EditText wishes = findViewById(R.id.enterText);            Log.i("Send email", "");            String[] TO = {""};            String[] CC = {""};            Intent emailIntent = new Intent(Intent.ACTION_SEND);            emailIntent.setData(Uri.parse("mailto:"));            emailIntent.setType("text/plain");            emailIntent.putExtra(Intent.EXTRA_EMAIL, TO);            emailIntent.putExtra(Intent.EXTRA_CC, CC);            emailIntent.putExtra(Intent.EXTRA_SUBJECT, "Your subject");            emailIntent.putExtra(Intent.EXTRA_TEXT,wishes.getText().toString());            try {                startActivity(Intent.createChooser(emailIntent, "Send mail..."));                finish();                Log.i("Finish", "");            } catch (android.content.ActivityNotFoundException ex) {                Toast.makeText(MainActivity.this, "There is no email client installed.",                        Toast.LENGTH_SHORT).show();            }        }    }